import java.util.*;
public class NumberPattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,j,rows=10;
		
		for(i=0;i<rows;i++)
		{
			int number=1;
			for(j=0;j<=i;j++)
			{
				System.out.print(number);
				number++;
			}
			System.out.println();
			
		}
	
	

}
}
